import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";

// expenses array

function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2020");

  const selectedYearHandler = (year) => {
    setFilteredYear(year);
  };

  // filtering the array based on the selected year; filteredExpenses is the new array, it holds only the filtered item (based on the year)
  // Now, this array is passed bellow to the map function


  const filteredExpenses = props.items;

  if(filteredYear === "All"){
    return filteredExpenses;
  }else{
     filteredExpenses = chosenYear.filter((expense) => {
      return expense.date.getFullYear().toString() === filteredYear;
    });
    
  }


  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onSelectedYear={selectedYearHandler}
        />
        {filteredExpenses.map((item) => (
          <ExpenseItem
            key={item.id}
            title={item.title}
            amount={item.amount}
            date={item.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
