import React from "react";
import "./Chart.css";
import ChartBar from "./ChartBar";

const Chart = (props) => {
    const dataPointValue = props.dataPoints.map((dataPoint) => dataPoint.value); // getting only the value from the array
    const totalMaximum =Math.max(...dataPointValue); // getting the biggest(maximum) amount 

  return (
    <div className="chart">
      {props.dataPoints.map((dataPoint) => ( //dataPoint is an object // dataPoints is an array of objects
        <ChartBar 
        key={dataPoint.label}
        value={dataPoint.value} 
        maxValue={totalMaximum} 
        label={dataPoint.Label} />
      ))}
    </div>
  );
};

export default Chart;
